import React, { useState, useEffect } from "react";
import fetchJSON from "../api";

export default function DepartmentForm({ department = {}, onClose }) {
  const [form, setForm] = useState({
    name: department.name || "",
    budget: department.budget || 0
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    setForm({
      name: department.name || "",
      budget: department.budget || 0
    });
  }, [department]);

  function validate() {
    const newErrors = {};

    if (!form.name.trim()) newErrors.name = "Department name is required.";
    if (!form.budget || form.budget < 0) newErrors.budget = "Budget must be 0 or higher.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }

  async function handleSubmit(e) {
    e.preventDefault();

    if (!validate()) return;

    try {
      const method = department._id ? "PUT" : "POST";
      const url = department._id
        ? `/departments/${department._id}`
        : "/departments";

      await fetchJSON(url, {
        method,
        body: JSON.stringify(form),
      });

      onClose();
    } catch (err) {
      alert(err.message);
    }
  }

  return (
    <div>
      <h3>{department._id ? "Edit Department" : "New Department"}</h3>

      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <label>Name</label>
          <input
            className="form-control"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
          />
          {errors.name && <small className="text-danger">{errors.name}</small>}
        </div>

        <div className="form-group mb-3">
          <label>Budget</label>
          <input
            className="form-control"
            type="number"
            value={form.budget}
            onChange={(e) => setForm({ ...form, budget: e.target.value })}
          />
          {errors.budget && <small className="text-danger">{errors.budget}</small>}
        </div>

        <button className="btn btn-primary" type="submit">Save</button>
        <button
          className="btn btn-secondary ms-2"
          type="button"
          onClick={onClose}
        >
          Cancel
        </button>
      </form>
    </div>
  );
}
