// src/components/DepartmentForm.jsx
import React, { useEffect, useState } from 'react';
import fetchJSON from '../api';

export default function DepartmentForm({ department = {}, onClose }) {
  const [form, setForm] = useState({ name: department.name || '', budget: department.budget || '' });
  const [error, setError] = useState('');
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    setForm({ name: department.name || '', budget: department.budget || '' });
    setError('');
  }, [department]);

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');

    // Client-side validation
    if (!form.name || !form.name.trim()) {
      return setError('Department name is required');
    }
    const budgetNum = parseFloat(form.budget);
    if (Number.isNaN(budgetNum) || budgetNum <= 0) {
      return setError('Budget must be a number greater than 0');
    }

    setSaving(true);
    try {
      const method = department._id ? 'PUT' : 'POST';
      const url = department._id ? `/departments/${department._id}` : '/departments';
      await fetchJSON(url, { method, body: { name: form.name.trim(), budget: budgetNum } });
      onClose();
    } catch (err) {
      // show friendly message
      const msg = (err && err.message) ? err.message : 'Failed to save';
      setError(msg.includes('duplicate') || msg.includes('E11000') ? 'Department name already exists' : msg);
    } finally {
      setSaving(false);
    }
  }

  return (
    <div className="card p-3 shadow-sm">
      <h5 className="mb-3">{department._id ? 'Edit Department' : 'New Department'}</h5>

      {error && <div className="alert alert-danger">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input
            className="form-control"
            value={form.name}
            onChange={e => setForm({ ...form, name: e.target.value })}
            disabled={saving}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Budget</label>
          <input
            className="form-control"
            type="number"
            step="0.01"
            value={form.budget}
            onChange={e => setForm({ ...form, budget: e.target.value })}
            disabled={saving}
          />
        </div>

        <div className="d-flex">
          <button type="submit" className="btn btn-success me-2" disabled={saving}>
            {saving ? 'Saving...' : 'Save'}
          </button>
          <button type="button" className="btn btn-secondary" onClick={onClose} disabled={saving}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
