import React, { useEffect, useState } from 'react';
import { fetchJSON } from '../api';
import EmployeeForm from '../components/EmployeeForm';

export default function EmployeesPage() {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [filterDept, setFilterDept] = useState('');
  const [editing, setEditing] = useState(null);

  async function load(deptId) {
    const deps = await fetchJSON('/departments');
    setDepartments(deps);
    const url = deptId ? `/employees?departmentId=${deptId}` : '/employees';
    const emps = await fetchJSON(url);
    setEmployees(emps);
  }

  useEffect(() => { load(); }, []);

  async function handleFilter(e) {
    e.preventDefault();
    await load(filterDept);
  }

  async function handleDelete(id) {
    if (!window.confirm('Are you sure?')) return;
    await fetchJSON(`/employees/${id}`, { method: 'DELETE' });
    await load(filterDept);
  }

  return (
    <div>
      <h1>Employees</h1>
      <button onClick={() => setEditing({})}>New Employee</button>

      <form onSubmit={handleFilter} style={{ marginTop: 10 }}>
        <label>Filter by Department: </label>
        <select value={filterDept} onChange={e => setFilterDept(e.target.value)}>
          <option value="">All</option>
          {departments.map(d => <option key={d._id} value={d._id}>{d.name}</option>)}
        </select>
        <button type="submit">Filter</button>
      </form>

      <table>
        <thead><tr><th>Full Name</th><th>Email</th><th>Department</th><th>Salary</th><th>Actions</th></tr></thead>
        <tbody>
          {employees.map(emp => (
            <tr key={emp._id}>
              <td>{emp.first_name} {emp.last_name}</td>
              <td>{emp.email}</td>
              <td>{emp.department?.name || 'No Dept'}</td>
              <td>{emp.salary}</td>
              <td>
                <button onClick={() => setEditing(emp)}>Edit</button>
                <button onClick={() => handleDelete(emp._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editing && (
        <div className="modal">
          <EmployeeForm
            employee={editing}
            departments={departments}
            onClose={() => { setEditing(null); load(filterDept); }}
          />
        </div>
      )}
    </div>
  );
}
