// src/components/EmployeeForm.jsx
import React, { useEffect, useState } from 'react';
import fetchJSON from '../api';

export default function EmployeeForm({ employee = {}, departments = [], onClose }) {
  const [form, setForm] = useState({
    first_name: employee.first_name || '',
    last_name: employee.last_name || '',
    email: employee.email || '',
    salary: employee.salary || '',
    department: employee.department?._id || '',
  });
  const [error, setError] = useState('');
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    setForm({
      first_name: employee.first_name || '',
      last_name: employee.last_name || '',
      email: employee.email || '',
      salary: employee.salary || '',
      department: employee.department?._id || '',
    });
    setError('');
  }, [employee]);

  function isEmailValid(value) {
    // simple check, server will enforce stronger validation
    return /\S+@\S+\.\S+/.test(value);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');

    // Client-side validation
    if (!form.first_name || !form.first_name.trim()) return setError('First name is required');
    if (!form.last_name || !form.last_name.trim()) return setError('Last name is required');
    if (!form.email || !form.email.trim()) return setError('Email is required');
    if (!isEmailValid(form.email.trim())) return setError('Invalid email format');
    const salaryNum = parseFloat(form.salary);
    if (Number.isNaN(salaryNum) || salaryNum <= 0) return setError('Salary must be a number greater than 0');
    if (!form.department) return setError('Please select a department');

    setSaving(true);
    try {
      const payload = {
        first_name: form.first_name.trim(),
        last_name: form.last_name.trim(),
        email: form.email.trim().toLowerCase(),
        salary: salaryNum,
        department: form.department,
      };
      const method = employee._id ? 'PUT' : 'POST';
      const url = employee._id ? `/employees/${employee._id}` : '/employees';
      await fetchJSON(url, { method, body: payload });
      onClose();
    } catch (err) {
      const msg = (err && err.message) ? err.message : 'Failed to save';
      setError(msg.includes('duplicate') || msg.includes('E11000') ? 'Email already exists' : msg);
    } finally {
      setSaving(false);
    }
  }

  return (
    <div className="card p-3 shadow-sm">
      <h5 className="mb-3">{employee._id ? 'Edit Employee' : 'New Employee'}</h5>

      {error && <div className="alert alert-danger">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="col-md-6 mb-3">
            <label className="form-label">First Name</label>
            <input className="form-control" value={form.first_name} onChange={e => setForm({ ...form, first_name: e.target.value })} disabled={saving} />
          </div>

          <div className="col-md-6 mb-3">
            <label className="form-label">Last Name</label>
            <input className="form-control" value={form.last_name} onChange={e => setForm({ ...form, last_name: e.target.value })} disabled={saving} />
          </div>
        </div>

        <div className="mb-3">
          <label className="form-label">Email</label>
          <input className="form-control" value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} disabled={saving} />
        </div>

        <div className="mb-3">
          <label className="form-label">Salary</label>
          <input className="form-control" type="number" step="0.01" value={form.salary} onChange={e => setForm({ ...form, salary: e.target.value })} disabled={saving} />
        </div>

        <div className="mb-3">
          <label className="form-label">Department</label>
          <select className="form-select" value={form.department} onChange={e => setForm({ ...form, department: e.target.value })} disabled={saving}>
            <option value="">Select Department</option>
            {departments.map(d => <option key={d._id} value={d._id}>{d.name}</option>)}
          </select>
        </div>

        <div className="d-flex">
          <button className="btn btn-primary me-2" type="submit" disabled={saving}>{saving ? 'Saving...' : 'Save'}</button>
          <button type="button" className="btn btn-secondary" onClick={onClose} disabled={saving}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
