import React, { useEffect, useState } from 'react';
import { fetchJSON } from '../api';
import DepartmentForm from '../components/DepartmentForm';

export default function DepartmentsPage() {
  const [depts, setDepts] = useState([]);
  const [editing, setEditing] = useState(null);

  async function load() {
    const data = await fetchJSON('/departments');
    setDepts(data);
  }

  useEffect(() => { load(); }, []);

  async function handleDelete(id) {
    if (!window.confirm('Delete department and its employees?')) return;
    await fetchJSON(`/departments/${id}`, { method: 'DELETE' });
    await load();
  }

  return (
    <div>
      <h1>Departments</h1>
      <button onClick={() => setEditing({})}>New Department</button>

      <table>
        <thead><tr><th>Name</th><th>Budget</th><th>Actions</th></tr></thead>
        <tbody>
          {depts.map(d => (
            <tr key={d._id}>
              <td>{d.name}</td>
              <td>{d.budget}</td>
              <td>
                <button onClick={() => setEditing(d)}>Edit</button>
                <button onClick={() => handleDelete(d._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editing && <div className="modal"><DepartmentForm department={editing} onClose={() => { setEditing(null); load(); }} /></div>}
    </div>
  );
}
