import React, { useEffect, useState } from 'react';
import { fetchJSON } from '../api';

export default function DepartmentForm({ department = {}, onClose }) {
  const [form, setForm] = useState({ name: department.name || '', budget: department.budget || 0 });
  const [error, setError] = useState('');

  useEffect(() => {
    setForm({ name: department.name || '', budget: department.budget || 0 });
  }, [department]);

  async function handleSubmit(e) {
    e.preventDefault();
    try {
      const method = department._id ? 'PUT' : 'POST';
      const url = department._id ? `/departments/${department._id}` : '/departments';
      await fetchJSON(url, { method, body: JSON.stringify(form) });
      onClose();
    } catch (err) {
      setError(err.message);
    }
  }

  return (
    <div>
      <h3>{department._id ? 'Edit Department' : 'New Department'}</h3>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <label>Name</label><br />
          <input value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} />
        </div>
        <div className="form-row">
          <label>Budget</label><br />
          <input type="number" step="0.01" value={form.budget} onChange={e => setForm({ ...form, budget: e.target.value })} />
        </div>
        <div style={{ marginTop: 8 }}>
          <button type="submit">Save</button>
          <button type="button" onClick={onClose} style={{ marginLeft: 8 }}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
