import React, { useEffect, useState } from 'react';
import fetchJSON from '../api';

export default function DepartmentForm({ department = {}, onClose }) {
  const [form, setForm] = useState({ name: '', budget: '' });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    setForm({
      name: department.name || '',
      budget: department.budget || '',
    });
  }, [department]);

  function validate() {
    let newErrors = {};

    if (!form.name.trim()) {
      newErrors.name = "Department name is required";
    } else if (form.name.length < 3) {
      newErrors.name = "Name must be at least 3 characters";
    }

    if (!form.budget) {
      newErrors.budget = "Budget is required";
    } else if (isNaN(form.budget) || form.budget <= 0) {
      newErrors.budget = "Budget must be a positive number";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!validate()) return;

    try {
      const method = department._id ? 'PUT' : 'POST';
      const url = department._id ? `/departments/${department._id}` : '/departments';

      await fetchJSON(url, { method, body: JSON.stringify(form) });
      onClose();
    } catch (err) {
      setErrors({ server: err.message });
    }
  }

  return (
    <div>
      <h3>{department._id ? 'Edit Department' : 'Add Department'}</h3>

      {errors.server && <div className="alert alert-danger">{errors.server}</div>}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Name</label>
          <input className="form-control"
            value={form.name}
            onChange={e => setForm({ ...form, name: e.target.value })}/>
          {errors.name && <small className="text-danger">{errors.name}</small>}
        </div>

        <div className="mb-3">
          <label>Budget</label>
          <input type="number" className="form-control"
            value={form.budget}
            onChange={e => setForm({ ...form, budget: e.target.value })}/>
          {errors.budget && <small className="text-danger">{errors.budget}</small>}
        </div>

        <button className="btn btn-primary" type="submit">Save</button>
        <button className="btn btn-secondary ms-2" onClick={onClose} type="button">Cancel</button>
      </form>
    </div>
  );
}
