import React, { useEffect, useState } from 'react';
import { fetchJSON } from '../api';

export default function DepartmentForm({ department = {}, onClose }) {
  const [form, setForm] = useState({ name: department.name || '', budget: department.budget || '' });
  const [error, setError] = useState('');

  useEffect(() => {
    setForm({ name: department.name || '', budget: department.budget || '' });
  }, [department]);

  async function handleSubmit(e) {
    e.preventDefault();

    if (!form.name.trim()) return setError("Department name is required");
    if (form.budget <= 0) return setError("Budget must be greater than 0");

    try {
      const method = department._id ? 'PUT' : 'POST';
      const url = department._id ? `/departments/${department._id}` : '/departments';
      await fetchJSON(url, { method, body: JSON.stringify(form) });
      onClose();
    } catch (err) {
      setError(err.message.includes("duplicate") ? "Department name already exists" : err.message);
    }
  }

  return (
    <div className="card p-3 shadow">
      <h4 className="mb-3">{department._id ? 'Edit Department' : 'New Department'}</h4>

      {error && <div className="alert alert-danger">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Name</label>
          <input className="form-control"
            value={form.name}
            onChange={e => setForm({ ...form, name: e.target.value })}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Budget</label>
          <input className="form-control" type="number"
            value={form.budget}
            onChange={e => setForm({ ...form, budget: e.target.value })}
          />
        </div>

        <button className="btn btn-success me-2">Save</button>
        <button type="button" className="btn btn-secondary" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
}
