import React, { useEffect, useState } from 'react';
import fetchJSON from '../api';

export default function EmployeeForm({ employee = {}, departments = [], onClose }) {

  const [form, setForm] = useState({
    first_name: employee.first_name || '',
    last_name: employee.last_name || '',
    email: employee.email || '',
    salary: employee.salary || '',
    department: employee.department?._id || ''
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    setForm({
      first_name: employee.first_name || '',
      last_name: employee.last_name || '',
      email: employee.email || '',
      salary: employee.salary || '',
      department: employee.department?._id || ''
    });
  }, [employee]);

  function validate() {
    const newErrors = {};

    if (!form.first_name.trim()) newErrors.first_name = "First name is required.";
    if (!form.last_name.trim()) newErrors.last_name = "Last name is required.";

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!form.email.trim()) newErrors.email = "Email is required.";
    else if (!emailRegex.test(form.email)) newErrors.email = "Invalid email format.";

    if (!form.salary || form.salary <= 0) newErrors.salary = "Salary must be greater than 0.";

    if (!form.department) newErrors.department = "Please select a department.";

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  }

  async function handleSubmit(e) {
    e.preventDefault();
    if (!validate()) return;

    try {
      const method = employee._id ? 'PUT' : 'POST';
      const url = employee._id ? `/employees/${employee._id}` : '/employees';

      await fetchJSON(url, { method, body: JSON.stringify(form) });
      onClose();
    } catch (err) {
      alert(err.message);
    }
  }

  return (
    <div>
      <h3>{employee._id ? 'Edit Employee' : 'New Employee'}</h3>

      <form onSubmit={handleSubmit}>

        <div className="form-group mb-2">
          <label>First Name</label>
          <input
            className="form-control"
            value={form.first_name}
            onChange={(e) => setForm({ ...form, first_name: e.target.value })}
          />
          {errors.first_name && <small className="text-danger">{errors.first_name}</small>}
        </div>

        <div className="form-group mb-2">
          <label>Last Name</label>
          <input
            className="form-control"
            value={form.last_name}
            onChange={(e) => setForm({ ...form, last_name: e.target.value })}
          />
          {errors.last_name && <small className="text-danger">{errors.last_name}</small>}
        </div>

        <div className="form-group mb-2">
          <label>Email</label>
          <input
            className="form-control"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
          />
          {errors.email && <small className="text-danger">{errors.email}</small>}
        </div>

        <div className="form-group mb-2">
          <label>Salary</label>
          <input
            className="form-control"
            type="number"
            step="0.01"
            value={form.salary}
            onChange={(e) => setForm({ ...form, salary: e.target.value })}
          />
          {errors.salary && <small className="text-danger">{errors.salary}</small>}
        </div>

        <div className="form-group mb-3">
          <label>Department</label>
          <select
            className="form-select"
            value={form.department}
            onChange={(e) => setForm({ ...form, department: e.target.value })}
          >
            <option value="">Select Department</option>
            {departments.map((d) => (
              <option key={d._id} value={d._id}>{d.name}</option>
            ))}
          </select>
          {errors.department && <small className="text-danger">{errors.department}</small>}
        </div>

        <button className="btn btn-primary" type="submit">Save</button>
        <button className="btn btn-secondary ms-2" type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
}
