import React, { useEffect, useState } from 'react';
import { fetchJSON } from '../api';

export default function EmployeeForm({ employee = {}, departments = [], onClose }) {
  const [form, setForm] = useState({
    first_name: employee.first_name || '',
    last_name: employee.last_name || '',
    email: employee.email || '',
    salary: employee.salary || '',
    department: employee.department?._id || ''
  });

  const [error, setError] = useState('');

  useEffect(() => {
    setForm({
      first_name: employee.first_name || '',
      last_name: employee.last_name || '',
      email: employee.email || '',
      salary: employee.salary || '',
      department: employee.department?._id || ''
    });
  }, [employee]);

  async function handleSubmit(e) {
    e.preventDefault();

    if (!form.first_name) return setError("First name is required");
    if (!form.last_name) return setError("Last name is required");
    if (!form.email) return setError("Email is required");
    if (!form.email.includes("@")) return setError("Invalid email format");
    if (!form.salary || form.salary <= 0) return setError("Salary must be greater than 0");
    if (!form.department) return setError("Department selection required");

    try {
      const method = employee._id ? 'PUT' : 'POST';
      const url = employee._id ? `/employees/${employee._id}` : '/employees';
      await fetchJSON(url, { method, body: JSON.stringify(form) });
      onClose();
    } catch (err) {
      setError(err.message.includes("duplicate") ? "Email already exists" : err.message);
    }
  }

  return (
    <div className="card p-3 shadow">
      <h4 className="mb-3">{employee._id ? "Edit Employee" : "New Employee"}</h4>

      {error && <div className="alert alert-danger">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="mb-2">
          <label>First Name</label>
          <input className="form-control" value={form.first_name} onChange={e => setForm({ ...form, first_name: e.target.value })} />
        </div>

        <div className="mb-2">
          <label>Last Name</label>
          <input className="form-control" value={form.last_name} onChange={e => setForm({ ...form, last_name: e.target.value })} />
        </div>

        <div className="mb-2">
          <label>Email</label>
          <input className="form-control" value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} />
        </div>

        <div className="mb-2">
          <label>Salary</label>
          <input className="form-control" type="number" value={form.salary} onChange={e => setForm({ ...form, salary: e.target.value })} />
        </div>

        <div className="mb-3">
          <label>Department</label>
          <select className="form-select" value={form.department} onChange={e => setForm({ ...form, department: e.target.value })}>
            <option value="">Select Department</option>
            {departments.map(d => <option key={d._id} value={d._id}>{d.name}</option>)}
          </select>
        </div>

        <button className="btn btn-primary me-2">Save</button>
        <button type="button" className="btn btn-secondary" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
}
