import React, { useEffect, useState } from 'react';
import { fetchJSON } from '../api';
import EmployeeForm from '../components/EmployeeForm';

export default function EmployeesPage() {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [filterDept, setFilterDept] = useState('');
  const [editing, setEditing] = useState(null);

  async function load(deptId) {
    const deps = await fetchJSON('/departments');
    setDepartments(deps);
    const url = deptId ? `/employees?departmentId=${deptId}` : '/employees';
    const emps = await fetchJSON(url);
    setEmployees(emps);
  }

  useEffect(() => { load(); }, []);

  async function handleFilter(e) {
    e.preventDefault();
    await load(filterDept);
  }

  async function handleDelete(id) {
    if (!window.confirm('Are you sure?')) return;
    await fetchJSON(`/employees/${id}`, { method: 'DELETE' });
    await load(filterDept);
  }

  return (
    <div className="container mt-4">
      <h2 className="text-primary mb-3">Employees</h2>

      <button className="btn btn-success mb-3" onClick={() => setEditing({})}>
        ➕ New Employee
      </button>

      <form className="row g-3 mb-3" onSubmit={handleFilter}>
        <div className="col-md-4">
          <label className="form-label">Filter by Department</label>
          <select className="form-select"
            value={filterDept}
            onChange={e => setFilterDept(e.target.value)}
          >
            <option value="">All</option>
            {departments.map(d => <option key={d._id} value={d._id}>{d.name}</option>)}
          </select>
        </div>
        <div className="col-md-2 align-self-end">
          <button className="btn btn-primary w-100" type="submit">Search</button>
        </div>
      </form>

      <table className="table table-striped table-hover">
        <thead className="table-dark">
          <tr><th>Name</th><th>Email</th><th>Department</th><th>Salary</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {employees.map(emp => (
            <tr key={emp._id}>
              <td>{emp.first_name} {emp.last_name}</td>
              <td>{emp.email}</td>
              <td>{emp.department?.name || 'No Dept'}</td>
              <td>₹ {emp.salary}</td>
              <td>
                <button className="btn btn-warning btn-sm me-2" onClick={() => setEditing(emp)}>Edit</button>
                <button className="btn btn-danger btn-sm" onClick={() => handleDelete(emp._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editing && (
        <div className="modal fade show d-block" style={{ background: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog">
            <div className="modal-content p-3">
              <EmployeeForm employee={editing} departments={departments} onClose={() => { setEditing(null); load(filterDept); }} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
